import React from 'react'
import './Button.css'

const Button = props => {

    let classes = ['button']

    if (theme === 'white') {
        classes = ['button', 'whiteButton']
    }
    else if (theme === 'blue') {
        classes = ['button', 'blueButton']
    }

    return (
        <div className={classes.join(' ')} style={props.style}>
            {props.children}
        </div>
    )
}

export default Button
